<?php/** * Created by PhpStorm. * User: Jmango * Date: 3/23/19 * Time: 10:40 AM */class PwaManifestService extends BaseService{    private $pwaSetting = 'JM_PWA_SETTING';    public function doExecute()    {        if ($this->isGetMethod()) {            $pwaManifest = new PwaManifestResponse();            $pwaSetting = json_decode(Configuration::get($this->pwaSetting));            if ($pwaSetting->appName) {                $pwaManifest->name = $pwaSetting->appName;                $pwaManifest->short_name = $pwaSetting->appName;            }            if ($pwaSetting->appIcon) {                array_push($pwaManifest->icons, new IconModelResponse('192x192', '', $pwaSetting->appIcon));                array_push($pwaManifest->icons, new IconModelResponse('512x512', '', $pwaSetting->appIcon));            }            $this->response = $pwaManifest;        }    }}